namespace SpriteKind {
    export const Flappy = SpriteKind.create()
}
controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    if (sprites.readDataBoolean(intro, "gameSelect") == true) {
        if (Selector.x == 290) {
            sprites.setDataBoolean(intro, "gameSelect", false)
            Selector.destroy()
            callFlappyBird()
        }
    }
    if (sprites.readDataBoolean(Selector, "Flappy") == true) {
        FlappySprite.vy = -100
    }
})
controller.left.onEvent(ControllerButtonEvent.Pressed, function () {
    if (sprites.readDataBoolean(intro, "gameSelect") == true) {
        if (Selector.x != 290 - 64 * 6) {
            Selector.x += -64
        }
    }
})
sprites.onDestroyed(SpriteKind.Flappy, function (sprite) {
    game.over(false)
})
